package It.Ihu.unit02.main;

public class Task02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			double a = 5.9;
			double b = 3.11;
			double stiepen = 2;
			
			double c;
			double ploszcza;
			
			c = Math.sqrt(Math.pow(a,stiepen) + Math.pow(b,stiepen));
			ploszcza = (a * b) / 2.0;
			
			System.out.println("c=" +c);
			System.out.println("ploszcza=" +ploszcza);
			
	}

}
